import React, { useState, useEffect } from 'react';
import { getAuctions } from '../services/api';
import AuctionCard from '../components/AuctionCard';
import './HomePage.css';

const HomePage = () => {
  const [auctions, setAuctions] = useState([]);
  const [loading, setLoading] = useState(true);
  // Eliminamos el estado de filtro ya que no lo necesitaremos

  useEffect(() => {
    const fetchAuctions = async () => {
      try {
        const data = await getAuctions();
        console.log('Datos recibidos de la API:', data);
        setAuctions(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching auctions:', error);
        setLoading(false);
      }
    };

    fetchAuctions();
  }, []);

  // Simplificamos esta función para que siempre devuelva todas las subastas
  const getAllAuctions = () => {
    return auctions;
  };

  const allAuctions = getAllAuctions();

  return (
    <div className="home-page">
      <div className="hero-section">
        <h1>Subastas Online</h1>
        <p>Encuentra artículos únicos y haz tus ofertas</p>
      </div>
      
      {/* Eliminamos la sección de filtros */}
      
      {loading ? (
        <div className="loading">Cargando subastas...</div>
      ) : allAuctions.length > 0 ? (
        <div className="auctions-grid">
          {allAuctions.map(auction => (
            <div key={auction.id} className="auction-item">
              <AuctionCard auction={auction} />
            </div>
          ))}
        </div>
      ) : (
        <div className="no-auctions">
          No hay subastas disponibles.
        </div>
      )}
    </div>
  );
};

export default HomePage;
